export const initialState = {
    mainPosts: [{
        id: 1,
        user:{
            id:1,
            nickname:'meno',
        },
        content: '게시글 #해시태그 #익스프레스',
        Images:[
            {
                src:''
            },
            {
                src:''
            },
            {
                src:''
            }
        ],
        Comments:[{
                User:{
                    nickname:'nero',
                },
                content:'덧글을 달다.',
            },
            {
                User:{
                    nickname:'nero2',
                },
                content:'덧글을 달다2.'
        }]
    }],
    imagePaths: [],
    postAdded: false,
};

//게시글 작성하는 액션
const ADD_POST = 'ADD_POST';
export const addPost = {
    type: ADD_POST,
}


const reducer = (state = initialState, action)=>{
    switch (action.type){
        case ADD_POST:
        default:
            return state;

    }
};

export default reducer;
