import { EllipsisOutlined, HeartOutlined, MessageOutlined, RetweetOutlined } from "@ant-design/icons";
import { Button,Card, Popover } from "antd";
import ButtonGroup from "antd/es/button/button-group";
import { useSelector } from "react-redux";
import PropTypes from 'react-redux';

const PostCard = ({post}) => {
    const {me} = useSelector((state)=>state.user);
    return(
        <div>
            <Card cover={post.Images[0] && <PostImages images={post.Images}/>}  
            actions={[ <RetweetOutlined key={retweet}/>,<HeartOutlined key={heart}/>,
            <MessageOutlined key={comment}/>,<Popover key={more} content={( <ButtonGroup>
                <Button>수정</Button>  <Button type="danger">삭제</Button>  <Button>신고</Button>
            </ButtonGroup>)}> <EllipsisOutlined/> </Popover>,]}>
                <Image/>
                <Content/>
             
            </Card>
            <CommendForm/>
            <Comments/>
        </div>
    );
};

PostCard.propTypes={
    post: propTypes.shape({
        id:propTypes.number,
        User:propTypes.object,
    }).isRequired,
};

export default PostCard;
