import { EllipsisOutlined, HeartOutlined, MessageOutlined, RetweetOutlined } from "@ant-design/icons";
import { Avatar, Button,Card, Popover } from "antd";

import { useSelector } from "react-redux";
import PropTypes from 'prop-types';
import PostImages from './PostImages';
import post from '../reducers/post';

const PostCard = ({post}) => {
    // const {me} = useSelector((state)=>state.user);
    // const id = me?.id; //옵셔널 체이닝 연산자

    const id = useSelector((state)=>state.user.me?.id);//있나 없나

    return(
        <div>
            <Card cover={post.Images[0] && <PostImages images={post.Images}/>}  
            actions={[ <RetweetOutlined key="retweet"/>,<HeartOutlined key="heart"/>,
            <MessageOutlined key="comment"/>,<Popover key="more" content={( 
            <Button.Group>
                {id && post.User.id === id ? (
                    <>
                       <Button>수정</Button>  
                        <Button type="danger">삭제</Button>  
                    </>
                ): <Button>신고</Button> }
             
            </Button.Group>)}> <EllipsisOutlined/> </Popover>,]}>

                <Card.Meta avatar={<Avatar>{post.User.nickname[0]}</Avatar>} title={post.User.nickname}  description={post.content}/>
                <Image/>
                <Content/>
             
            </Card>
            {/* <CommendForm/>
            <Comments/> */}
        </div>
    );
};

PostCard.PropTypes={
    post: PropTypes.shape({
        id:PropTypes.number,
        User:PropTypes.object,
        content: PropTypes.string,
        createAt: PropTypes.object,
        Conmments: PropTypes.arrayOf(PropTypes.object),
        Images: PropTypes.arrayOf(PropTypes.object),
    }).isRequired,
};

export default PostCard;
