//초기상태
const initialState = {
    user: {
        isLoggedIn:false,
        user: null,
        signUpDate: {},
        loginData: {},
    },
    post:{
        mainPosts: [],
    }
};
// const initialState = {
//     name:'mememe',
//     age:27,
//     password:'metoo'
// };

//action creator
//사용 예시 store.dispatch(changeNickname('바뀔 닉네임'))
const changeNickname = (data) =>{
    return{
        type: 'CHANGE_NICKNAME',
        data,
    }
};
//로그인 액션
const login = (data) =>{
    return{
        type: 'LOG_IN',
        data,
    }
};

//async action creator 비동기 액션 크리에이터

// const changeNickname = {
//     type: 'CHANGE_NICKNAME',
//     data: 'youu',
// }


//(이전상태, 액션)=> 다음상태를 만들어내는 함수
// const rootReducer = (( state = initialState, action)=>{
//     switch(action.type){
//         case 'CHANGE_NICKNAME':
//             return{
//                 ...state,
//                 name: action.data,
//             }
//     }
// });

const rootReducer = (( state = initialState, action)=>{
    switch(action.type){
        case 'LOG_IN':
            return{
                ...state,
                user:{
                    ...state.user,
                    isLoggedIn: true,
                }
            }
    }
});


export default rootReducer;
