import {Button, Form, Input} from 'antd';
import { useCallback, useRef, useState } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { addPost } from '../reducers/post';

const PostForm = ()=>{
    const {imagePaths} = useSelector((state)=> state.post);
    const [text, setText] = useState('');
    const dispatch = useDispatch();
    const imageInput = useRef();

    const onSubmit = useCallback(()=>{
        dispatch(addPost);
    }, []);
    const onChangeText = useCallback((e)=>{
        setText(e.target.value);
        setText('');
    }, []);

    const onClickImageUpload = useCallback(() => {   //프로팔 올리기
        imageInput.current.click();              //imageInput을 클릭한걸로 만든다
      }, [imageInput.current])

    return(
        <Form style={{margin:'10px 0 20px'}} encType="multipart/form-data" onFinish={onSubmit}>
            <Input.TextArea value={text} onChange={onChangeText} maxLength={140} placeholder="무슨일 있었냐"/>
            <div>
                <inqut type="file" multiple hidden ref={imageInput} />
                <Button onClick={onClickImageUpload}>이미지 업로드</Button>
                <Button type='primary' style={{float:'right'}} htmlType="submit">짹짹</Button>
            </div>
            <div>
                {imagePaths.map((v)=>(
                    <div key={v} style={{display:'inline-bolck'}}>
                        <img src={v} style={{width:'200px'}} alt={v} />
                        <div>
                            <Button>제거</Button>
                        </div>
                    </div>
                ))}
            </div>
        </Form>
    )
};
export default PostForm;
